package arraypractice;

public class ArrayExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arryexp = {1,2,3,4};
		
		for(int i=0;i<arryexp.length;i++) {
			
			if(arryexp.equals(1)) {
				System.out.println("My array value equal to 1");
			}else {
				System.out.println("My array value equal to morethan 1");
			}
		System.out.println("Value is"+ arryexp[i]);
		
	}
}
}



