package arraypractice;

public class ArrayExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] arrayexp2= {"Srikanth","Perla"};
		
		for(int i=0;i<arrayexp2.length;i++) {
			if(arrayexp2[i].equals("Srikanth")) {
				System.out.println("My first name is Srikanth");
				
			}else {
				System.out.println("My last name is Perla");
				
			}
			
		}
	}

}
